mapper.select:
select 

from program_item_visit
where lineseat = #{lineseat,jdbcType=VARCHAR}
  and material_no = #{materialNo,jdbcType=VARCHAR}
  and program_id = #{programId,jdbcType=VARCHAR}

mapper.delete:
delete from program_item_visit
where lineseat = #{lineseat,jdbcType=VARCHAR}
  and material_no = #{materialNo,jdbcType=VARCHAR}
  and program_id = #{programId,jdbcType=VARCHAR}
